import { useState } from "react"

function Square({ value }) {
  return <button className="square" onClick={handleClick}>{ value }</button>
}


export default function Board() {
const [squares, setSquares] = useState(Array(9).fill(null))

  function handleClick(value) {
    setSquares(squares[value] = "X")
  }


  return (
    <>
    <div className="board-row">
      <Square value={squares[0]} onClick={handleClick(0)} />
      <Square value={squares[1]} onClick={handleClick(0)} />
      <Square value={squares[2]} onClick={handleClick(0)} />
    </div>
    <div className="board-row">
      <Square value={squares[3]} onClick={handleClick(0)} />
      <Square value={squares[4]} onClick={handleClick(0)} />
      <Square value={squares[5]} onClick={handleClick(0)} />
    </div>
    <div className="board-row">
      <Square value={squares[6]} onClick={handleClick(0)} />
      <Square value={squares[7]} onClick={handleClick(0)} />
      <Square value={squares[8]} onClick={handleClick(0)} />
    </div>
    </>
  )
}
